import React, { Component } from 'react'

export class Postform extends Component {
    constructor(props){
        super(props);
        this.state = {
            title: '',
            body: ''
        };
    }

    render() {
        return (
            <div>
                <h1>Add Post</h1>
                <form action="#">
                    <div>
                        <label >Title</label>
                        <br/>
                        <input type="text" name='title' value=
                        {this.this.state.title}/>
                    </div>
                    <div>
                        <label >Body</label>
                        <br/>
                        <textarea name='body' value=
                        {this.state.body}/>
                    </div>
                    <br/>
                </form>
            </div>
        )
    }
}

export default Postform
